<?phpnamespace AppBundle\Controller;use AppBundle\Entity\Coaches;use AppBundle\Form\CoachAddType;use Doctrine\Bundle\DoctrineBundle\Command\Proxy\ClearQueryCacheDoctrineCommand;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\Form\Extension\Core\Type\SubmitType;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\HttpFoundation\Request;Class AdminCoachController extends Controller{    /**     * @Route("/admin/{team}/coach/add", defaults={"team" = "Bulgaria"}, name="adm_co_add")     */    public function coachAddAction($team, Request $request)    {        $playerObj = new Coaches();        $form = $this->createForm(CoachAddType::class, $playerObj);        $form->add('modify', SubmitType::class, [            'label' => 'save'        ]);        $form->handleRequest($request);        if ($form->isValid()) {            $formString = $form->getData()->getTeam()->getCountry();            $em = $this->getDoctrine()->getManager();            $em->persist($playerObj);            $em->flush();            return $this->redirect($this->generateUrl('team', ['team' => $formString]));        }        return $this->render(':soccer:adminPlayer.html.twig', ['team' => $team, 'form' => $form->createView()]);    }    /**     * @Route("/admin/{team}/coach/{id}/mod", defaults={"team" = "Bulgaria"}, name="adm_co_mod")     * requirements={"id": "\d+"})     */    public function coachModShowAction($team, $id, Request $request)    {        $em = $this->getDoctrine()->getManager();        $playerObj = $em->getRepository('AppBundle:Coaches')->find($id);        $form = $this->createForm(CoachAddType::class, $playerObj);        $form->add('modify', SubmitType::class);        if ($request->getMethod() == 'POST') {            $form->handleRequest($request);            $formString = $form->getData()->getTeam()->getCountry();            if ($form->isValid()) {                $em->flush();                return $this->redirect($this->generateUrl('team', ['team' => $formString]));            }        }        return $this->render(':soccer:adminPlayer.html.twig', ['team' => $team, 'form' => $form->createView()]);    }    /**     * @Route("/admin/{team}/coach/{id}/del", defaults={"team" = "Bulgaria"}, name="adm_co_del")     * requirements={"id": "\d+"})     */    public function coachDelAction($team, $id, Request $request)    {        if ($id) {            $em = $this->getDoctrine()->getManager();            $entity = $em->getRepository('AppBundle:Coaches')->find($id);            if (!$id) {                throw $this->createNotFoundException('Unable to find Coach');            }            $em->remove($entity);            $em->flush();        }        return $this->redirect($this->generateUrl('team', ['team' => $team]));    }    /**     * @Route("/admin/{team}/coach/show.{id}", defaults={"team" = "Bulgaria"}, name="adm_co_sh_id",     * requirements={"id": "\d+"})     */    public function coachShowAction($team, $id, Request $request)    {    }    /**     * @Route("/admin/{team}/coach/showall", defaults={"team" = "Bulgaria"}, name="adm_co_sh")     */    public function coachShowAllAction($team, Request $request)    {    }}