<?phpnamespace AppBundle\Controller;use Doctrine\Bundle\DoctrineBundle\Command\Proxy\ClearQueryCacheDoctrineCommand;use Symfony\Bundle\FrameworkBundle\Controller\Controller;use Sensio\Bundle\FrameworkExtraBundle\Configuration\Route;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\HttpFoundation\Request;Class TeamController extends Controller{    /**     * @Route("/team/{team}", defaults={"team" = "Bulgaria"})     */    public function teamShowAction($team, Request $request)    {        $res = $this->trueCountry($team);        $list = $this->listStr('player', $team, 'player', '10');        $listCoach = $this->listStr('coach', $team, 'coach', '4');        $listPlayers = 'list of PLAYERS:<br><ul>' . $list . '</ul>';        $listCoaches = 'list of COACHES:<br><ul>' . $listCoach . '</ul>';        $lastMatches = '<p>Last played mathes:<br> For the first time, 24 sides will contest the UEFA European Championship when it takes place between 10 June and 10 July 2016.The Stade de France will stage the finalThe inaugural final tournament in France in 1960 was a four-team affair, involving four matches played over five days at two venues. For UEFA EURO 2016, France\'s third finals having won on home turf in 1984, 24 sides will contest 51 games over 32 days at ten venues â€“ in Bordeaux, Lens, Lille, Lyon, Marseille, Nice, Paris, Saint-Denis, Saint-Etienne and Toulouse.</p>';        //$main = '<a href="http://'.$request->headers->get('Host').'/app_dev.php">Main</a><br>';        return new Response('<html><body>' . $this->mainUrl($request) . $res . '<br>' . $listPlayers . $listCoaches . $lastMatches . '</body></html>');    }    /**     * @Route("/team/{team}/player/{player}", defaults={"team" = "Bulgaria"}, name="player")     */    public function playerAction($team, $player, Request $request)    {        $strTeam = 'Team: ' . $team . '<br>';        $strPlayer = '   Player: ' . $player . '<br>';        $biography = '<p>Biography:<br>  Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium consequuntur debitis dolorem eaque eius enim harum inventore magni non odio optio porro quis repellat repellendus, saepe sint soluta tenetur vero?<br>';        $res = $strTeam . $strPlayer . $biography;        //$main = '<a href="http://'.$request->headers->get('Host').'/app_dev.php">Main</a><br>';        return new Response('<html><body>' . $this->mainUrl($request) . $res . '</body></html>');    }    /**     * @Route("/team/{team}/coach/{coach}", defaults={"team" = "Bulgaria"}, name="coach")     */    public function coachAction($team, $coach, Request $request)    {        $strTeam = 'Team: ' . $team . '<br>';        $strCoach = '   Coach: ' . $coach . '<br>';        $biography = '<p>Biography:<br>  Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusantium consequuntur debitis dolorem eaque eius enim harum inventore magni non odio optio porro quis repellat repellendus, saepe sint soluta tenetur vero?<br>';        $res = $strTeam . $strCoach . $biography;        //$main = '<a href="http://'.$request->headers->get('Host').'/app_dev.php">Main</a><br>';        return new Response('<html><body>' . $this->mainUrl($request) . $res . '</body></html>');    }     /**     * @Route("/flag/{flag}", defaults={"flag" = "Bulgaria"}, name="flag")     */    public function flagAction($flag, Request $request)    {        $res = 'Flag: ' . $this->trueCountry($flag) . '<br>';        $about = '<p>About<span style="color: #759e1a"> ' . $flag . ':</span><br> Lorem ipsum dolor sit amet, consectetur adipisicing elit. Accusamus accusantium adipisci at, culpa facilis fuga id illo incidunt iste iure minus modi nihil, odio pariatur, possimus quae qui vel voluptatum.</p>';        //$main = '<a href="http://'.$request->headers->get('Host').'/app_dev.php">Main</a><br>';        return new Response('<html><body>' . $this->mainUrl($request) . $res . $about . '</body></html>');    }    private function mainUrl(Request $request)    {        return '<a href="http://'.$request->headers->get('Host').'/app_dev.php">Main</a><br>';    }    private function listStr($nameRoute, $team, $person, $num)    {        $urlList = '';        for ($i = 1; $i <= $num; $i++) {            $url = $this->generateUrl(                $nameRoute,                array('team' => $team, $person => $person . $i)            );            $li = "<li><a href=\"" . $url . "\">" . $person . $i . "</a></li>";            $urlList = $urlList . $li;        }        return $urlList;    }    private function trueCountry($country)    {        $teamAll = array('France', 'Iceland', 'Czech Republic', 'Turkey', 'Belgium', 'Wales', 'Spain', 'Slovakia',            'Germany', 'Poland', 'England', 'Switzerland', 'Northern', 'Ireland', 'Romania', 'Austria', 'Russia',            'Italy', 'Croatia', 'Portugal', 'Albania', 'Hungary', 'Republic of Ireland', 'Ukraine', 'Sweden');        if (in_array($country, $teamAll)) {            $resp = '<span style="color: #759e1a">' . $country . '</span> in array';        } else {            $resp = '<span style="color: #759e1a">' . $country . '</span> not in array';        };        return $resp;    }}